INSERT INTO `article` VALUES (74, 24, 2, 12, NULL, 1, 0, NULL, '商家入驻', NULL, '2018-12-30 12:07:03', NULL, 1, 1, 1, NULL, NULL, '<p>\r\n	平台服务/规则\r\n</p>\r\n<p>\r\n	平台服务：（即入住支持）\r\n</p>\r\n<p>\r\n	宣传物料：海报、宣传单页、贴纸、展架\r\n</p>\r\n<p>\r\n	技术支持：网络平台的设计、建设、维护、升级、标准商品数据、数据库建设维护\r\n</p>\r\n<p>\r\n	物流支持：协助招募管理配送人员\r\n</p>\r\n<p>\r\n	培训支持：后台操作培训、网络运营培训\r\n</p>\r\n<p>\r\n	活动推广：线上推广、社区会员招募、节日活动策划、促销活动策划、持续吸引客流\r\n</p>\r\n<p>\r\n	平台支持：PC电商平台、入驻移动手机端、大数据\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	平台规则：（即入驻条件）\r\n</p>\r\n<p>\r\n	意识观念：认同“翼商城”经营理念，认同行业互联网发展趋势。\r\n</p>\r\n<p>\r\n	门店租期：门店租赁有效期限一年以上。\r\n</p>\r\n<p>\r\n	门店面积：要求门店经营面积30平米以上。\r\n</p>\r\n<p>\r\n	平台费用：按翼商城现行服务标准执行。\r\n</p>', NULL, 255, 1, 0, '2018-12-30 12:07:53', '2018-12-30 12:07:53', NULL);
