<script type='text/javascript'>
    //点击下一步
    $().ready(function () {
        var validator = $("#{{ $uuid }}").find("#GoodsModel").validate();
// 验证规则，此验证规则会影响编辑器中JavaScript的的格式化操作
        $.validator.addRules($("#{{ $uuid }}").find("#client_rules").html());

        $("#{{ $uuid }}").find("#btn_next_step").click(function () {
            if (!validator.form()) {
                return;
            }
            var goods_ids = $("#goods_ids").val();
            var data = $("#GoodsModel").serializeJson();
            $.post('/goods/list/set-goods-moq', data, function (result) {
                if (result.code == 0) {
// 关闭对话框
                    $("#{{ $uuid }}").parents(".modal").find(".close").click();
// 显示信息
                    $.msg(result.message, {
                        time: 1500
                    }, function () {
                        if (typeof (tablelist) != "undefined" && tablelist) {
                            tablelist.load();
                        } else {
                            $.go('/goods/list');
                        }
                    });
                } else {
                    $.msg(result.message, {
                        time: 5000
                    });
                }
            }, "json");
        })
    });
</script>