<script type="text/javascript">
    //
</script><script>

    //点击下一步
    $().ready(function() {
        var validator = $("#{{ $uuid }}").find("#GoodsModel").validate();
// 验证规则，此验证规则会影响编辑器中JavaScript的的格式化操作
        $.validator.addRules($("#client_rules").html());

        $("#{{ $uuid }}").find("#btn_next_step").click(function() {
            if (!validator.form()) {
                return;
            }
            var goods_ids = $("#{{ $uuid }}").find("#goods_ids").val();
            var keywords = $("#{{ $uuid }}").find("#keywords").val();
            var change_mech = $("#goodsmodel-change_mech :checked").val();

            if(keywords == '')
            {
                $.msg('关键词不能为空',{
                    time:2000
                });

                return;
            }

            $.loading.start();
            $.post("/goods/list/set-keywords", {
                goods_ids: goods_ids,
                keywords: keywords,
                change_mech: change_mech
            }, function(result) {
                if (result.code == 0) {
// 关闭对话框
// $("#{{ $uuid }}").parents(".layui-layer").find(".layui-layer-close").click();
                    $.closeAll();

                    if(typeof(tablelist) != 'undefined' && tablelist){
                        tablelist.load();
                    }

// 显示信息
                    $.msg(result.message, {
                        time: 1500
                    }, function() {
                        if(typeof(tablelist) == 'undefined'){
                            $.go('/goods/list');
                        }
                    });
                } else {
                    $.msg(result.message, {
                        time: 5000
                    });
                }
            }, "json").always(function() {
                $.loading.stop();
            });
        });
    });

    //
</script>