        $.validator.addRules($("#client_rules").html());
        $("#{{ $uuid }}").find("#btn_submit").click(function() {
            if (!validator.form()) {
                return;
            }
//加载提示
            $.loading.start();
            $("#{{ $uuid }}").find("#OrderModel").submit();
        });

        $("#{{ $uuid }}").find("#region_code_containter").regionselector({
            value: '{{ $order_info['region_code'] }}',
            select_class: "form-control ipt m-r-5",
            change: function(value, names, is_last) {
                $("#{{ $uuid }}").find("#region_code").val(value);
            }
        });

        $("#{{ $uuid }}").find(".btn-default").click(function() {
            $(".layui-layer-shade").hide();
            $(".layui-layer").hide();
        });
    });

    //
</script>